import React, { useState, useEffect } from "react";
import axios from "axios";
import { Container, Button } from "@mui/material";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import UserTable from "./UserTable";
import UserFormDialog from "./UserFormDialog";
import ConfirmDialog from "./components/ConfirmDialog";

const App = () => {
  const [users, setUsers] = useState([]);
  const [openFormDialog, setOpenFormDialog] = useState(false);
  const [currentUser, setCurrentUser] = useState(null);
  const [openConfirmDialog, setOpenConfirmDialog] = useState(false);
  const [deleteUserId, setDeleteUserId] = useState(null);

  useEffect(() => {
    fetchUsers();
  }, []);

  // Fetch all users
  const fetchUsers = async () => {
    try {
      const response = await axios.get(":http://localhost:3000/users");
      setUsers(response.data.users);
    } catch (error) {
      console.error("Error fetching users:", error);
    }
  };

  // Open form dialog for add/edit
  const handleOpenFormDialog = (user = null) => {
    setCurrentUser(user);
    setOpenFormDialog(true);
  };

  // Close form dialog
  const handleCloseFormDialog = () => {
    setOpenFormDialog(false);
  };

  // Open confirm dialog for delete
  const handleOpenConfirmDialog = (id) => {
    setDeleteUserId(id);
    setOpenConfirmDialog(true);
  };

  // Close confirm dialog
  const handleCloseConfirmDialog = () => {
    setOpenConfirmDialog(false);
    setDeleteUserId(null);
  };

  // Handle user delete
  const handleDeleteUser = async () => {
    try {
      await axios.delete(`http://localhost:3000/users/${deleteUserId}`);
      setUsers(users.filter((user) => user.id !== deleteUserId));
      toast.success("User deleted successfully!");
      handleCloseConfirmDialog();
    } catch (error) {
      console.error("Error deleting user:", error);
    }
  };

  // Handle form submission for add/edit
  const handleFormSubmit = async (formData) => {
    try {
      if (currentUser) {
        // Update user
        await axios.patch(`http://localhost:3000/users/${currentUser.id}`, formData);
        setUsers(
          users.map((user) =>
            user.id === currentUser.id ? { ...user, ...formData } : user
          )
        );
      } else {
        // Add user
        const response = await axios.post("http://localhost:3000/users", formData);
        setUsers([...users, response.data]);
        toast.success("User added successfully!");
      }
      handleCloseFormDialog();
    } catch (error) {
      console.error("Error saving user:", error);
    }
  };

  return (
    <Container>
      <h1>User Management</h1>
      <Button variant="contained" color="primary" onClick={() => handleOpenFormDialog()}>
        Add User
      </Button>
      <UserTable users={users} onEdit={handleOpenFormDialog} onDelete={handleOpenConfirmDialog} />
      <UserFormDialog
        open={openFormDialog}
        onClose={handleCloseFormDialog}
        onSubmit={handleFormSubmit}
        user={currentUser}
      />
      <ConfirmDialog
        open={openConfirmDialog}
        onClose={handleCloseConfirmDialog}
        onConfirm={handleDeleteUser}
        user={users.find((user) => user.id === deleteUserId)}
      />
      <ToastContainer />
    </Container>
  );
};

export default App;
