import React, { useState, useEffect } from "react";
import { Dialog, DialogActions, DialogContent, DialogTitle, Button, TextField, List, ListItem, ListItemText } from "@mui/material";

const UserFormDialog = ({ open, onClose, onSubmit, user, existingUsers }) => {
  const [formData, setFormData] = useState({ name: "", email: "" });
  const [emailError, setEmailError] = useState(false);
  const [emailErrorMsg, setEmailErrorMsg] = useState("");
  const [nameError, setNameError] = useState(false);
  const [nameErrorMsg, setNameErrorMsg] = useState("");
  
  // For search functionality
  const [searchTerm, setSearchTerm] = useState({ name: "", email: "" });
  const [filteredUsers, setFilteredUsers] = useState(existingUsers);

  useEffect(() => {
    if (user) {
      setFormData(user); // Load user details for editing
    } else if (open) {
      setFormData({ name: "", email: "" }); // Clear the form for adding a new user
    }
  }, [user, open]);

  useEffect(() => {
    // Filter the users based on the search term
    const filtered = existingUsers.filter(
      (existingUser) =>
        existingUser.name.toLowerCase().includes(searchTerm.name.toLowerCase()) &&
        existingUser.email.toLowerCase().includes(searchTerm.email.toLowerCase())
    );
    setFilteredUsers(filtered);
  }, [searchTerm, existingUsers]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
    if (name === "email") {
      validateEmail(value);
    }
    if (name === "name") {
      validateName(value);
    }
  };

  const handleSearchChange = (e) => {
    const { name, value } = e.target;
    setSearchTerm({ ...searchTerm, [name]: value }); // Update search terms
  };

  const handleSubmit = () => {
    const isNameValid = validateName(formData.name);
    const isEmailValid = validateEmail(formData.email);

    if (isNameValid && isEmailValid && !isEmailDuplicate(formData.email) && !isNameDuplicate(formData.name)) {
      onSubmit(formData);
      onClose(); // Close the popup after saving
    }
  };

  const validateEmail = (email) => {
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    const isValid = emailRegex.test(email);
    setEmailError(!isValid);
    setEmailErrorMsg(isValid ? "" : "Enter a valid email address.");
    return isValid;
  };

  const validateName = (name) => {
    const nameRegex = /^[A-Za-z\s]+$/;
    const isValid = nameRegex.test(name);
    setNameError(!isValid);
    setNameErrorMsg(isValid ? "" : "Name must only contain alphabets.");
    return isValid;
  };

  const isEmailDuplicate = (email) => {
    // Check for duplicate email, excluding the currently edited user's email
    const duplicate = existingUsers.some(
      (existingUser) => existingUser.email === email && (!user || existingUser.email !== user.email)
    );
    if (duplicate) {
      setEmailError(true);
      setEmailErrorMsg("This email is already in use.");
    }
    return duplicate;
  };

  const isNameDuplicate = (name) => {
    // Check for duplicate name, excluding the currently edited user's name
    const duplicate = existingUsers.some(
      (existingUser) => existingUser.name === name && (!user || existingUser.name !== user.name)
    );
    if (duplicate) {
      setNameError(true);
      setNameErrorMsg("This name is already in use.");
    }
    return duplicate;
  };

  return (
    <Dialog open={open} onClose={onClose}>
      <DialogTitle>{user ? "Edit User" : "Add User"}</DialogTitle>
      <DialogContent>
        <TextField
          label="Name"
          fullWidth
          margin="normal"
          name="name"
          value={formData.name}
          onChange={handleChange}
          error={nameError}
          helperText={nameErrorMsg}
        />
        <TextField
          label="Email"
          fullWidth
          margin="normal"
          name="email"
          value={formData.email}
          onChange={handleChange}
          error={emailError}
          helperText={emailErrorMsg}
        />

        {/* Search Fields for Name and Email */}
        <TextField
          label="Search Name"
          fullWidth
          margin="normal"
          name="name"
          value={searchTerm.name}
          onChange={handleSearchChange}
        />
        <TextField
          label="Search Email"
          fullWidth
          margin="normal"
          name="email"
          value={searchTerm.email}
          onChange={handleSearchChange}
        />

        {/* Filtered User List */}
        <List>
          {filteredUsers.length > 0 ? (
            filteredUsers.map((user, index) => (
              <ListItem key={index}>
                <ListItemText primary={`${user.name} (${user.email})`} />
              </ListItem>
            ))
          ) : (
            <ListItem>
              <ListItemText primary="No users found" />
            </ListItem>
          )}
        </List>
      </DialogContent>
      <DialogActions>
        <Button onClick={onClose} color="secondary">
          Cancel
        </Button>
        <Button onClick={handleSubmit} color="primary">
          Save
        </Button>
      </DialogActions>
    </Dialog>
  );
};

export default UserFormDialog;
